retArray = float[];
counter = 0;
for each in serviceDetail_servArray{
	price = 0.0;
	flex = (atof(substring(flex_servArray[counter], 0, -1)) / 100) + 1.0;
	rate = string(rateLevel_servArray[counter]);
	recordset = bmql("SELECT Price FROM ServiceDetails WHERE Service = $each AND Rate = $rate");
	for record in recordset{
		price = atof(get(record, "Price"));
	}

	retArray[counter] = (price*resources_servArray[counter]*flex*hours_servArray[counter]);

	counter = counter + 1;	
}
return retArray ;

